SELECT genre.id_genre 'id_genre', genre.name 'name_genre', distrib.id_distrib 'id_distrib', distrib.name 'name_distrib', film.title 'title_film' FROM film LEFT JOIN distrib ON distrib.id_distrib = film.id_distrib LEFT JOIN genre ON genre.id_genre = film.id_genre WHERE (film.id_genre > 3 AND film.id_genre < 9);
SELECT film.id_genre,genre.name,film.id_distrib,distrib.name,film.title from film LEFT JOIN genre on film.id_genre = genre.id_genre LEFT JOIN distrib on film.id_distrib = distrib.id_distrib WHERE film.id_genre >= 4 AND film.id_genre <= 8